import Image from 'next/image';

const Issuer = ({ issuer }) => {
  if (!issuer) {
    return null;
  }

  let image;
  switch (issuer) {
    case 'visa':
      image =
        'data:image/svg+xml;base64,';
      break;

    case 'visaelectron':
      image =
        'data:image/svg+xml;base64,';
      break;

    case 'mastercard':
      image =
        'data:image/svg+xml;base64,';
      break;

    case 'amex':
      image =
        'data:image/svg+xml;base64,';
      break;

    case 'discover':
      image =
        'data:image/svg+xml;base64,';
      break;

    case 'maestro':
      image =
        'data:image/svg+xml;base64,';
      break;
  }

  return <Image width="100" height="25" src={image} />;
};

export default Issuer;
